import React from "react";

import {
  Typography,
  Card,
  makeStyles,
  Paper,
  Button,
  CardContent,
  CardActions,
} from "@material-ui/core";

import { Link } from "react-router-dom";

import Map from "../addons/Map";
const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: "16px",
    padding: "16px 64px",

    [theme.breakpoints.down("sm")]: {
      marginTop: 0,
      padding: "4px",
    },
  },
  welcome: {
    padding: "16px",
    marginBottom: "32px",

    [theme.breakpoints.down("sm")]: {
      padding: "4px",
      margin: "16px",
    },
  },
  actions: {
    justifyContent: "right",
  },
  callToAction: {
    textDecoration: "none",
  },
}));
const HomePage = () => {
  const classes = useStyles();

  return (
    <Paper width={1} className={classes.root}>
      <Card className={classes.welcome}>
        <CardContent>
          <Typography variant="h5" color="primary">
            Faciliter la pratique de la classe dehors et l’apprentissage dehors
            pour les acteurs de l’éducation.
          </Typography>
        </CardContent>
        <CardActions className={classes.actions}>
          <Link to="/manifest" className={classes.callToAction}>
            <Button color="primary" variant="contained">
              Voir le manifeste
            </Button>
          </Link>
        </CardActions>
      </Card>

      <Map />

      <CardActions className={classes.actions}>
        <Link to="/manifest" className={classes.callToAction}>
          <Button color="primary" variant="contained">
            Voir le manifeste
          </Button>
        </Link>
      </CardActions>
    </Paper>
  );
};

export default HomePage;
