import React from "react";
import { UrlField, DateField, SelectField } from "react-admin";
import {
  Hero,
  Show,
  MainList,
  MarkdownField,
} from "@semapps/archipelago-layout";
import EventTitle from "./EventTitle";
import { eventType, organizerType } from "constants.js";

const EventShow = (props) => (
  <Show title={<EventTitle />} {...props}>
    <>
      <Hero>
        <DateField source="pair:startDate" showTime />
        <DateField source="pair:endDate" showTime />
        <UrlField source="cd:registrationPage" />
        <UrlField source="cd:meetingPage" />

        <TextFiled source="cd:organizerName" fullWidth />
        <TextFiled source="pair:hasLabel" fullWidth />

        <SelectField
          source="pair:HasType"
          validate={required()}
          choices={eventType}
        />
        <SelectField
          source="cd:organizerTypee"
          validate={required()}
          choices={organizerType}
        />

        <TextInput
          source="cd:registrationPage"
          validate={urlValidation}
          fullWidth
        />
        <TextInput source="cd:meetingPage" validate={urlValidation} fullWidth />
        <TextInput source="cd:documentedAt" fullWidth />
      </Hero>
      <MainList>
        <MarkdownField source="pair:description" />
      </MainList>
    </>
  </Show>
);

export default EventShow;
