import React from "react";
import EducationNationalForm from "./specializedForm/EducationNationalForm";

import { SimpleForm, RadioButtonGroupInput } from "react-admin";

export const PersonForm = ({ mode, record, ...rest }) => {
  console.log({ props2: rest, rec: record["pair:hasType"] });
  if (!record || !record["pair:hasType"]) {
    return (
      <SimpleForm
        {...rest}
        // redirect="show"
        toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
      >
        <RadioButtonGroupInput
          source="pair:hasType"
          choices={[
            { id: "EducationNationale", name: "Education Nationale" },
            { id: "lifestyle", name: "Lifestyle" },
            { id: "photography", name: "Photography" },
          ]}
        />
      </SimpleForm>
    );
  }
  if (record["pair:hasType"] === "EducationNationale") {
    return <EducationNationalForm {...{ record, ...rest }} />;
  }
  return <p>TODO</p>;
};

export default PersonForm;
