    <SimpleForm
      {...rest}
      redirect="show"
      toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
    >
      <LargeLabel>Profil</LargeLabel>
      <TextInput
        source="pair:label"
        label="Prénom ou Nom"
        validate={required()}
        fullWidth
      />
      <AutocompleteInput
        source="TODO"
