      />
      <TextInput
        source="cd:structureLocality"
        label="Commune de l’école"
        validate={required()}
        fullWidth
      />
      <LargeLabel>Pratique de la classe dehors</LargeLabel>
      <ReferenceQuickCreateInput
        reference="Place"
        source="pair:hasLocation"
        selectOptionText="pair:label"
        label="Où faite-vous classe dehors?"
        perPage={1000}
      >
        <PairLocationInput
          label="Adresse"
          source="pair:hasPostalAddress"
          fullWidth
        />
        <TextInput
          label="Nom"
          source="pair:label"
          validate={required()}
          fullWidth
        />
        <MarkdownInput
          label="Description"
          source="pair:description"
          multiline
          fullWidth
        />
      </ReferenceQuickCreateInput>

      <TextInput source="cd:practiceTime" fullWidth />
      <TextInput source="cd:practiceFrequency" fullWidth />

      <TextInput source="pair:webPage" fullWidth />

      <LargeLabel>Contact</LargeLabel>
      {mode === "create" && (
        <TextInput
          source="pair:e-mail"
          helperText="Votre adresse mail n'apparaîtra pas publiquement. On pourra vous écrire via un formulaire de contact."
          fullWidth
        />
