import React from "react";

import {
  Typography,
  Box,
  makeStyles,
  Paper,
  Button,
  CardContent,
  CardActions,
  useMediaQuery,
} from "@material-ui/core";

import { Link } from "react-router-dom";

import Map from "../addons/Map";
const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: "16px",
    padding: "16px 8px",
    flex: "1 1 0px",
    [theme.breakpoints.down("sm")]: {
      marginTop: 0,
      padding: "4px",
    },
  },
  welcomeTop: {
    display: "flex",
    flexDirection: "row",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
      alignItems: "center",
    },
  },
  map: {
    flexGrow: 1,
    width: "100%",
  },
  message: {
    flexGrow: 1,
    maxWidth: "500px",
    padding: "16px",
    marginBottom: "32px",

    [theme.breakpoints.down("sm")]: {
      padding: "4px",
      margin: "16px",
    },
  },
  right: {
    justifyContent: "right",
  },

  center: {
    justifyContent: "center",
  },
  callToAction: {
    textDecoration: "none",
  },
}));
const HomePage = () => {
  const classes = useStyles();
  const xs = useMediaQuery((theme) => theme.breakpoints.down("xs"));
  return (
    <Paper className={classes.root}>
      <div className={classes.welcomeTop}>
        <Box className={classes.message}>
          <CardContent>
            <Typography variant="h5" color="primary">
              Faciliter la pratique de la classe dehors et l’apprentissage
              dehors pour les acteurs de l’éducation.
            </Typography>
          </CardContent>
          <CardActions className={classes.right}>
            <Link
              to="/Page/https%3A%2F%2Fdata.classe-dehors.org%2Fpages%2Fmanifeste"
              className={classes.callToAction}
            >
              <Button color="primary" variant="contained">
                Voir le manifeste
              </Button>
            </Link>
          </CardActions>
        </Box>
        <Box className={classes.map}>
          <Map height={xs ? 200 : 400} />
          <CardActions className={classes.center}>
            <Link to="/Person/create" className={classes.callToAction}>
              <Button color="primary" variant="contained">
                Rejoindre la communauté
              </Button>
            </Link>
          </CardActions>
        </Box>
      </div>
    </Paper>
  );
};

export default HomePage;
