import React from "react";
import { DateField } from "react-admin";
import { Avatar } from "@material-ui/core";
import { List, SimpleList } from "@semapps/archipelago-layout";
import EventIcon from "@material-ui/icons/Event";
import MarkdownIntroduction from "../../markdown/MarkdownIntroduction";
import { isAfter } from "date-fns";

const postRowStyle = (record, index) => {
  console.log(new Date(record["pair:endDate"]));
  const pastEvent = isAfter(new Date(record["pair:endDate"]), new Date());
  return {
    backgroundColor: pastEvent ? "#efe" : "gray",
  };
};

const EventList = (props) => (
  <>
    <MarkdownIntroduction pageId="agenda-introduction" />
    <List
      {...props}
      title="Calendrier"
      sort={{ field: "pair:startDate", order: "DESC" }}
      actions={false}
    >
      <SimpleList
        primaryText={(record) => record["pair:label"]}
        secondaryText={(record) => (
          <>
            Du&nbsp;
            <DateField record={record} source="pair:startDate" showTime />
            &nbsp;au&nbsp;
            <DateField record={record} source="pair:endDate" showTime />
          </>
        )}
        leftAvatar={() => (
          <Avatar width="100%">
            <EventIcon />
          </Avatar>
        )}
        linkType="show"
        rowStyle={postRowStyle}
      />
    </List>
  </>
);

export default EventList;
