import React from "react";
import { SimpleForm, TextInput, required } from "react-admin";
import MarkdownInput from "ra-input-markdown";
import PairLocationInput from "../../pair/PairLocationInput";

const validatePlaceForm = (values) => {
  const errors = {};
  console.log(values["pair:hasPostalAddress"]);
  if (!values["pair:hasPostalAddress"]) {
    errors["pair:hasPostalAddress"] = "The firstName is required";
    errors["pair:label"] = "The firstName is required";
    errors["pair:description"] = "The firstName is required";
  }
  return errors;
};

export const PlaceForm = ({ mode, ...rest }) => (
  <SimpleForm {...rest} redirect="show" validate={validatePlaceForm}>
    <PairLocationInput
      source="pair:hasPostalAddress"
      fullWidth
      validate={[required()]}
    />
    <TextInput source="pair:label" fullWidth />
    <MarkdownInput
      multiline
      source="pair:description"
      defaultValue="test"
      fullWidth
      validate={[required()]}
      validation={{ required: true }}
      {...rest}
    />
  </SimpleForm>
);

export default PlaceForm;
