import React from "react";
import {
  SimpleForm,
  TextInput,
  SelectInput,
  AutocompleteInput,
  Toolbar,
  required,
} from "react-admin";
import MarkdownInput from "ra-input-markdown";
import { LargeLabel } from "@semapps/archipelago-layout";
import { ReferenceInput } from "@semapps/semantic-data-provider";
import { teachingLevel, structureType } from "../../../constants";
import ReferenceQuickCreateInput from "../../../pair/ReferenceQuickCreateInput";
import PairLocationInput from "../../../pair/PairLocationInput";

// * **Education nationale**
//     * Profil
//         * Prénom ou nom
//             * champ obligatoire
//         * Académie
//             * champ obligatoire
//             * Liste des 30 ou champ libre pour autre pays
//         * Poste occupé : réponse libre
//             * champ obligatoire
//         * Niveau : maternelle, élémentaire, collège, lycée, supérieur
//             * champ obligatoire
//         * Type d'école : école publique, école privée sous contrat, ecole privée hors contrat
//             * champ obligatoire
//         * Nom de l'école
//             * champ obligatoire
//         * Commune de l'école
//             * champ obligatoire
//     * Pratique de la classe dehors
//         * Où faite-vous classe dehors ?
//             * Commentaire sous la question "C'est ce lieu qui apparaîtra sur la carte"
//             * question obligatoire
//         * Depuis quand pratiquez-vous la classe dehors ?
//         * A quelle fréquence ?
//         * Avez-vous un site internet, un blog et/ou un compte sur un réseau social ?
//         * Pour partager une pratique pédagogique avec un collègue et nourrir la bibiliothèque collaborative, cliquez [ici](https://wiki.faire-ecole.org/wiki/Sp%C3%A9cial:AjouterPage?form=Activit%C3%A9&page_name=)
//     * Contact
//         * Adresse mail
//         * champ obligatoire
//         *

<select name="svg-select" class="svg-select" aria-label="Choisir une académie">
  <option value="" disabled="disabled" selected="selected" hidden="">
    Choisir une académie
  </option>
  <option value="aix-marseille">Aix-Marseille</option>
  <option value="amiens">Amiens</option>
  <option value="besancon">Besançon</option>
  <option value="bordeaux">Bordeaux</option>
  <option value="clermont-ferrand">Clermont-Ferrand</option>
  <option value="corse">Corse</option>
  <option value="creteil">Créteil</option>
  <option value="dijon">Dijon</option>
  <option value="grenoble">Grenoble</option>
  <option value="guadeloupe">Guadeloupe</option>
  <option value="guyane">Guyane</option>
  <option value="la-reunion">La Réunion</option>
  <option value="lille">Lille</option>
  <option value="limoges">Limoges</option>
  <option value="lyon">Lyon</option>
  <option value="martinique">Martinique</option>
  <option value="mayotte">Mayotte</option>
  <option value="montpellier">Montpellier</option>
  <option value="nancy-metz">Nancy-Metz</option>
  <option value="nantes">Nantes</option>
  <option value="nice">Nice</option>
  <option value="normandie">Normandie</option>
  <option value="nouvelle-caledonie">Nouvelle-Calédonie</option>
  <option value="orleans-tours">Orléans-Tours</option>
  <option value="paris">Paris</option>
  <option value="poitiers">Poitiers</option>
  <option value="polnesie-francaise">Polynésie française</option>
  <option value="reims">Reims</option>
  <option value="rennes">Rennes</option>
  <option value="saint-pierre-et-miquelon">Saint-Pierre-et-Miquelon</option>
  <option value="strasbourg">Strasbourg</option>
  <option value="toulouse">Toulouse</option>
  <option value="versailles">Versailles</option>
  <option value="wallis-et-futuna">Wallis-et-Futuna</option>
</select>;

export const EducationNationalForm = ({ mode, ...rest }) => {
  console.log({ props2: rest });
  return (
    <SimpleForm
      {...rest}
      redirect="show"
      toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
    >
      <TextInput
        source="pair:label"
        label="Prénom ou Nom"
        validate={required()}
        fullWidth
      />

      <AutocompleteInput
        source=""
        label="Académie"
        validate={required()}
        choices={[
          { id: "programming", name: "Programming" },
          { id: "lifestyle", name: "Lifestyle" },
          { id: "photography", name: "Photography" },
        ]}
      />
      <ReferenceInput
        reference="Type"
        source="pair:hasType"
        filter={{ a: "pair:AgentType" }}
      >
        <SelectInput optionText="pair:label" />
      </ReferenceInput>
      <SelectInput source="cd:teachingLevel" choices={teachingLevel} />
      {/* <TextInput source="cd:subjects" fullWidth /> */}
      <LargeLabel>Structure</LargeLabel>
      <SelectInput source="cd:structureType" choices={structureType} />
      <TextInput source="cd:structureName" fullWidth />
      <TextInput source="cd:structureLocality" fullWidth />
      <LargeLabel>Pratique</LargeLabel>
      <ReferenceQuickCreateInput
        reference="Place"
        source="pair:hasLocation"
        selectOptionText="pair:label"
        perPage={1000}
      >
        <PairLocationInput
          label="Adresse"
          source="pair:hasPostalAddress"
          fullWidth
        />
        <TextInput
          label="Nom"
          source="pair:label"
          validate={required()}
          fullWidth
        />
        <MarkdownInput
          label="Description"
          source="pair:description"
          multiline
          fullWidth
        />
      </ReferenceQuickCreateInput>
      <TextInput source="cd:practiceTime" fullWidth />
      <TextInput source="cd:practiceFrequency" fullWidth />
      <TextInput source="cd:practiceSubjects" fullWidth />
      <LargeLabel>Communauté</LargeLabel>
      <TextInput source="cd:skills" fullWidth />
      <TextInput source="pair:webPage" fullWidth />
      <TextInput source="cd:needs" fullWidth />
      <TextInput source="cd:comments" fullWidth />
      <LargeLabel>Contact</LargeLabel>
      {mode === "create" && (
        <TextInput
          source="pair:e-mail"
          helperText="Votre adresse mail n'apparaîtra pas publiquement. On pourra vous écrire via un formulaire de contact."
          fullWidth
        />
      )}
      <TextInput source="pair:aboutPage" fullWidth />
    </SimpleForm>
  );
};

