import React from "react";
import EducationNationalForm from "./specializedForm/EducationNationalForm";
import AssociationForm from "./specializedForm/AssociationForm";
import CollectiviteForm from "./specializedForm/CollectiviteForm";
import LaboratoireDeRechercheForm from "./specializedForm/LaboratoireDeRechercheForm";
import { profileType } from "constants.js";
import {
  SimpleForm,
  Toolbar,
  RadioButtonGroupInput,
  FormDataConsumer,
  SelectInput,
} from "react-admin";
import { ReferenceInput } from "@semapps/semantic-data-provider";

const FilteredSelectInput = ({ choices, ...props }) => {
  return <SelectInput {...props} optionText="pair:label" />;
};
export const PersonForm = ({ mode, ...rest }) => {
  return (
    <SimpleForm
      {...rest}
      redirect="show"
      toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
    >
      <ReferenceInput
        reference="Type"
        source="pair:hasType"
        filter={{
          a: "pair:AgentType",
        }}
      >
        <FilteredSelectInput />
        {/* <RadioButtonGroupInput optionText="pair:label" /> */}
      </ReferenceInput>
      <FormDataConsumer fullWidth>
        {({ formData, ...rest }) => {
          console.log(formData["pair:hasType"]);
          if (formData["pair:hasType"] === "education_nationale") {
            return <EducationNationalForm {...rest} />;
          }
          if (formData["pair:hasType"] === "association") {
            return <AssociationForm />;
          }
          if (formData["pair:hasType"] === "collectivite") {
            return <CollectiviteForm />;
          }
          if (formData["pair:hasType"] === "laboratoire_de_recherche") {
            return <LaboratoireDeRechercheForm />;
          }
          return null;
        }}
      </FormDataConsumer>
    </SimpleForm>
  );
};

export default PersonForm;
