import React from "react";
import { SimpleForm, TextInput, required, useInput } from "react-admin";
import RAMarkdownInput from "ra-input-markdown";
import PairLocationInput from "../../pair/PairLocationInput";

const validatePlaceForm = (values) => {
  const errors = {};
  if (!values["pair:hasPostalAddress"]) {
    errors["pair:hasPostalAddress"] = "required";
  }
  if (!errors["pair:label"]) {
    errors["pair:label"] = "required";
  }
  if (!errors["pair:description"]) {
    errors["pair:description"] = "required";
  }

  errors.message = "toto";
  return errors;
};

export const PlaceForm = ({ mode, ...rest }) => (
  <SimpleForm {...rest} redirect="show" validate={validatePlaceForm}>
    <PairLocationInput
      source="pair:hasPostalAddress"
      fullWidth
      validate={[required()]}
    />
    <TextInput source="pair:label" fullWidth error={"aaa"} helperText={"eee"} />
    <RAMarkdownInput
      {...rest}
      multiline
      source="pair:description"
      defaultValue="test"
      fullWidth
      validate={[required()]}
      validation={{ required: true }}
      error={"aaa"}
      helperText={"eee"}
    />
  </SimpleForm>
);

export default PlaceForm;
