import React from "react";
import { FilterList, FilterListItem, FilterLiveSearch } from "react-admin";
import { Card, CardContent, makeStyles } from "@material-ui/core";
import { profileType } from "constants.js";

const useStyles = makeStyles((theme) => ({
  card: {
    paddingTop: 0,
    [theme.breakpoints.up("sm")]: {
      minWidth: "15em",
      marginLeft: "1em",
    },
    [theme.breakpoints.down("sm")]: {
      display: "none",
    },
  },
}));

const ProjectFilterSidebar = () => {
  const classes = useStyles();
  return (
    <Card className={classes.card}>
      <CardContent>
        <FilterLiveSearch />
        <FilterList label="Last visited">
          {profileType.map(({ id, name }) => (
            <FilterListItem label={name} value={id} />
          ))}
        </FilterList>
        <ReferenceFilter reference="Type" source="cd:personType" />
      </CardContent>
    </Card>
  );
};

export default ProjectFilterSidebar;
