import React from "react";
import EducationNationalForm from "./specializedForm/EducationNationalForm";

import {
  SimpleForm,
  Toolbar,
  RadioButtonGroupInput,
  FormDataConsumer,
  TextInput,
  required,
} from "react-admin";

export const PersonForm = ({ mode, record, ...rest }) => {
  return (
    <SimpleForm
      {...rest}
      // redirect="show"
      toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
    >
      <TextInput
        {...rest}
        source="pair:label"
        label="PrÃ©nom ou Nom"
        validate={required()}
        fullWidth
      />
      <RadioButtonGroupInput
        source="pair:hasType"
        choices={[
          { id: "EducationNationale", name: "Education Nationale" },
          { id: "lifestyle", name: "Lifestyle" },
          { id: "photography", name: "Photography" },
        ]}
      />
      <FormDataConsumer>
        {({ formData, ...rest }) => {
          if (formData["pair:hasType"] === "EducationNationale") {
            return <EducationNationalForm {...rest} />;
          }
          return <p>TODO {record["pair:hasType"]}</p>;
        }}
      </FormDataConsumer>
    </SimpleForm>
  );
};

export default PersonForm;
