import React, { useState } from "react";
import { Notification, Link } from "react-admin";
import {
  Container,
  Box,
  useMediaQuery,
  ThemeProvider,
  makeStyles,
  Typography,
} from "@material-ui/core";
import AppBar from "./AppBar";
import ScrollToTop from "./ScrollToTop";
import SideMenu from "./SideMenu";
import TwitterIcon from "@material-ui/icons/Twitter";

const useStyles = makeStyles((theme) => ({
  hero: {
    backgroundImage: `url('${process.env.PUBLIC_URL}/bandeau.png')`,
    backgroundSize: "contain",
    backgroundPosition: "bottom center",
  },
  userMenu: {
    float: "right",
    marginTop: theme.spacing(1),
    marginRight: theme.spacing(1),
    backgroundColor: theme.palette.grey["200"],
    "& button": {
      padding: "6px 12px",
    },
  },
  title: {
    position: "absolute",
    top: 180,
    textOverflow: "ellipsis",
    whiteSpace: "nowrap",
    overflow: "hidden",
    [theme.breakpoints.down("sm")]: {
      top: 70,
      left: 15,
      right: 50,
      fontSize: 22,
      zIndex: 10,
    },
  },
  footerLink: {
    color: theme.palette.grey["500"],
    textDecoration: "none",
    "&:hover": {
      textDecoration: "underline",
    },
  },
  footerIcon: {
    marginBottom: -5,
  },
}));

const menuItems = [
  { text: "Accueil", internal: true, link: "/" },
  {
    text: "Communauté",
    children: [
      { text: "Carte", internal: true, link: "/Place" },
      { text: "Annuaire", internal: true, link: "/Person" },
    ],
  },
  {
    text: "Ressources",
    children: [
      {
        text: "Proposer une ressources",
        internal: false,
        link: "/https://wiki.faire-ecole.org/wiki/Spécial:AjouterPage?form=Activité&page_name=",
      },
      {
        text: "Accéder à la bibliothèque",
        internal: false,
        link: "https://wiki.faire-ecole.org/wiki/Ressources-classe-dehors",
      },

      {
        text: "FAQ profs",
        internal: false,
        link: "https://wiki.faire-ecole.org/wiki/Foire_Aux_Questions_:_se_lancer_dans_la_classe_dehors",
      },
      {
        text: "Témoignages",
        internal: false,
        link: "https://wiki.faire-ecole.org/wiki/Témoignages_:_apprendre_dehors",
      },
      // {
      //   text: "FAQ collectivités",
      //   internal: false, link:
      //     "",
      // },
      {
        text: "Cours végétalisées",
        internal: false,
        link: "https://wiki.faire-ecole.org/wiki/Cour_de_récréation",
      },
    ],
  },
  { text: "Agenda", internal: true, link: "/Event" },
  // { text: "Qui Sommes-nous", internal: true,link: "/" },
];

const Layout = ({ appBar, logout, theme, children }) => {
  const classes = useStyles();
  const xs = useMediaQuery(theme.breakpoints.down("xs"));
  const [sidebarOpen, setSidebarOpen] = useState(false);
  return (
    <ThemeProvider theme={theme}>
      <ScrollToTop />
      <SideMenu
        menuItems={menuItems}
        sidebarOpen={sidebarOpen}
        setSidebarOpen={setSidebarOpen}
      />
      {!xs && <Box width={1} height="90px" className={classes.hero} />}
      {React.cloneElement(appBar, {
        logout,
        menuItems,
        setSidebarOpen,
      })}
      <Container maxWidth="lg" disableGutters={xs}>
        <Typography
          variant="h4"
          color="primary"
          className={classes.title}
          id="react-admin-title"
          component="h1"
        />
        <Box mb={{ xs: 1, sm: 2 }} mt={{ xs: 5, sm: 0 }}>
          {children}
        </Box>
        <Box mb={{ xs: 1, sm: 3 }} mr={{ xs: 2, sm: 0 }}>
          <Typography variant="subtitle2" color="textSecondary" align="right">
            <a
              href="https://www.virtual-assembly.org/semapps/"
              target="_blank"
              rel="noopener noreferrer"
              className={classes.footerLink}
            >
              Plateforme collaborative propulsée par SemApps
            </a>
            &nbsp;&nbsp;|&nbsp;&nbsp;
            <a
              href="https://7bfa4c43.sibforms.com/serve/MUIEAF8FDu9IC1HA_mL_uDKZVF4OWufDRlpBX4ZHN8Ej9FVKQuzKnPNph5IEusbuo0n21Gc17xTyrru-ogzXC8sZsJbPaEOT3vQNRIPqPo6pLud4xUqKTLoO46YKpCPrZ2wvHd0UoGohNBjo4NS6UrVgXXvTgWHzUVwF7OxEpYMAZI4JNhm_6bacAipNT07mQ-7ALfzsdoD7_iOb"
              target="_blank"
              rel="noopener noreferrer"
              className={classes.footerLink}
            >
              Newsletter
            </a>
            &nbsp;&nbsp;|&nbsp;&nbsp;
            <Link
              to="/Page/mentions-legales/show"
              className={classes.footerLink}
            >
              Mentions légales
            </Link>
            &nbsp;&nbsp;|&nbsp;&nbsp;
            <a
              href="mailto:contact@classe-dehors.org"
              className={classes.footerLink}
            >
              Contact
            </a>
            &nbsp;&nbsp;|&nbsp;&nbsp;
            <Link
              to="/Page/revue-de-presse/show"
              className={classes.footerLink}
            >
              Revue de presse
            </Link>
            {/* &nbsp;&nbsp;|&nbsp;&nbsp;
            <a
              href="https://twitter.com/ClasseDehors"
              target="_blank"
              rel="noopener noreferrer"
              className={classes.footerLink}
            >
              <TwitterIcon fontSize="small" className={classes.footerIcon} />
            </a> */}
          </Typography>
        </Box>
      </Container>
      {/* Required for react-admin optimistic update */}
      <Notification />
    </ThemeProvider>
  );
};

Layout.defaultProps = {
  appBar: <AppBar />,
};

export default Layout;
