const EducationNationalForm = [
  <LargeLabel>Profil</LargeLabel>,
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="pair:label"
      label="Prénom ou Nom"
      validate={required()}
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <AutocompleteInput
      {...rest}
      source="TODO"
      label="Académie"
      validate={required()}
      choices={academies}
    />
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="TODO"
      label="Poste occupé"
      validate={required()}
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <SelectInput
      {...rest}
      source="cd:teachingLevel"
      validate={required()}
      choices={teachingLevel}
    />
  ),
  ({ mode, ...rest }) => (
    <SelectInput
      {...rest}
      source="cd:structureType"
      label="Type d’école"
      validate={required()}
      choices={schoolType}
    />
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="cd:structureName"
      label="Nom de l’école"
      validate={required()}
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="cd:structureLocality"
      label="Commune de l’école"
      validate={required()}
      fullWidth
    />
  ),
  ({ mode, ...rest }) => <LargeLabel>Pratique de la classe dehors</LargeLabel>,
  ({ mode, ...rest }) => (
    <ReferenceQuickCreateInput
      {...rest}
      reference="Place"
      source="pair:hasLocation"
      selectOptionText="pair:label"
      label="Où faite-vous classe dehors?"
      perPage={1000}
      validate={required()}
      helperText="C’est ce lieu qui apparaîtra sur la carte"
    >
      <PairLocationInput
        label="Adresse"
        source="pair:hasPostalAddress"
        fullWidth
      />
      <TextInput
        label="Nom"
        source="pair:label"
        validate={required()}
        fullWidth
      />
      <MarkdownInput
        label="Description"
        source="pair:description"
        multiline
        fullWidth
      />
    </ReferenceQuickCreateInput>
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="cd:practiceTime"
      label="Depuis quand pratiquez-vous la classe dehors ?"
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="cd:practiceFrequency"
      label="A quelle fréquence ?"
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <TextInput
      {...rest}
      source="pair:webPage"
      label="Avez-vous un site internet, un blog et/ou un compte sur un réseau social ?"
      fullWidth
    />
  ),
  ({ mode, ...rest }) => (
    <>
      <LargeLabel>Contact</LargeLabel>
      <TextInput
        {...rest}
        source="pair:e-mail"
        label="Adresse mail"
        helperText="Votre adresse mail n'apparaîtra pas publiquement. On pourra vous écrire via un formulaire de contact."
        fullWidth
      />
    </>
  ),
];
export default EducationNationalForm;
