import React from "react";
import {
  SimpleForm,
  TextInput,
  SelectInput,
  AutocompleteInput,
  Toolbar,
  required,
} from "react-admin";
import MarkdownInput from "ra-input-markdown";
import { LargeLabel } from "@semapps/archipelago-layout";
import { ReferenceInput } from "@semapps/semantic-data-provider";
import { teachingLevel, structureType } from "../../../constants";
import ReferenceQuickCreateInput from "../../../pair/ReferenceQuickCreateInput";
import PairLocationInput from "../../../pair/PairLocationInput";

// * **Education nationale**
//     * Profil
//         * Prénom ou nom
//             * champ obligatoire
//         * Académie
//             * champ obligatoire
//             * Liste des 30 ou champ libre pour autre pays
//         * Poste occupé : réponse libre
//             * champ obligatoire
//         * Niveau : maternelle, élémentaire, collège, lycée, supérieur
//             * champ obligatoire
//         * Type d'école : école publique, école privée sous contrat, ecole privée hors contrat
//             * champ obligatoire
//         * Nom de l'école
//             * champ obligatoire
//         * Commune de l'école
//             * champ obligatoire
//     * Pratique de la classe dehors
//         * Où faite-vous classe dehors ?
//             * Commentaire sous la question "C'est ce lieu qui apparaîtra sur la carte"
//             * question obligatoire
//         * Depuis quand pratiquez-vous la classe dehors ?
//         * A quelle fréquence ?
//         * Avez-vous un site internet, un blog et/ou un compte sur un réseau social ?
//         * Pour partager une pratique pédagogique avec un collègue et nourrir la bibiliothèque collaborative, cliquez [ici](https://wiki.faire-ecole.org/wiki/Sp%C3%A9cial:AjouterPage?form=Activit%C3%A9&page_name=)
//     * Contact
//         * Adresse mail
//         * champ obligatoire
//         *

const academies = [
  [
    { id: "aix-marseille", name: "Aix-Marseille" },
    { id: "amiens", name: "Amiens" },
    { id: "besancon", name: "Besançon" },
    { id: "bordeaux", name: "Bordeaux" },
    { id: "clermont-ferrand", name: "Clermont-Ferrand" },
    { id: "corse", name: "Corse" },
    { id: "creteil", name: "Créteil" },
    { id: "dijon", name: "Dijon" },
    { id: "grenoble", name: "Grenoble" },
    { id: "guadeloupe", name: "Guadeloupe" },
    { id: "guyane", name: "Guyane" },
    { id: "la-reunion", name: "La Réunion" },
    { id: "lille", name: "Lille" },
    { id: "limoges", name: "Limoges" },
    { id: "lyon", name: "Lyon" },
    { id: "martinique", name: "Martinique" },
    { id: "mayotte", name: "Mayotte" },
    { id: "montpellier", name: "Montpellier" },
    { id: "nancy-metz", name: "Nancy-Metz" },
    { id: "nantes", name: "Nantes" },
    { id: "nice", name: "Nice" },
    { id: "normandie", name: "Normandie" },
    { id: "nouvelle-caledonie", name: "Nouvelle-Calédonie" },
    { id: "orleans-tours", name: "Orléans-Tours" },
    { id: "paris", name: "Paris" },
    { id: "poitiers", name: "Poitiers" },
    { id: "polnesie-francaise", name: "Polynésie française" },
    { id: "reims", name: "Reims" },
    { id: "rennes", name: "Rennes" },
    { id: "saint-pierre-et-miquelon", name: "Saint-Pierre-et-Miquelon" },
    { id: "strasbourg", name: "Strasbourg" },
    { id: "toulouse", name: "Toulouse" },
    { id: "versailles", name: "Versailles" },
    { id: "wallis-et-futuna", name: "Wallis-et-Futuna" },
  ],
];
export const EducationNationalForm = ({ mode, ...rest }) => {
  console.log({ props2: rest });
  return (
    <SimpleForm
      {...rest}
      redirect="show"
      toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
    >
      <TextInput
        source="pair:label"
        label="Prénom ou Nom"
        validate={required()}
        fullWidth
      />

      <AutocompleteInput
        source=""
        label="Académie"
        validate={required()}
        choices={academies}
      />
      <ReferenceInput
        reference="Type"
        source="pair:hasType"
        filter={{ a: "pair:AgentType" }}
      >
        <SelectInput optionText="pair:label" />
      </ReferenceInput>
      <SelectInput source="cd:teachingLevel" choices={teachingLevel} />
      {/* <TextInput source="cd:subjects" fullWidth /> */}
      <LargeLabel>Structure</LargeLabel>
      <SelectInput source="cd:structureType" choices={structureType} />
      <TextInput source="cd:structureName" fullWidth />
      <TextInput source="cd:structureLocality" fullWidth />
      <LargeLabel>Pratique</LargeLabel>
      <ReferenceQuickCreateInput
        reference="Place"
        source="pair:hasLocation"
        selectOptionText="pair:label"
        perPage={1000}
      >
        <PairLocationInput
          label="Adresse"
          source="pair:hasPostalAddress"
          fullWidth
        />
        <TextInput
          label="Nom"
          source="pair:label"
          validate={required()}
          fullWidth
        />
        <MarkdownInput
          label="Description"
          source="pair:description"
          multiline
          fullWidth
        />
      </ReferenceQuickCreateInput>
      <TextInput source="cd:practiceTime" fullWidth />
      <TextInput source="cd:practiceFrequency" fullWidth />
      <TextInput source="cd:practiceSubjects" fullWidth />
      <LargeLabel>Communauté</LargeLabel>
      <TextInput source="cd:skills" fullWidth />
      <TextInput source="pair:webPage" fullWidth />
      <TextInput source="cd:needs" fullWidth />
      <TextInput source="cd:comments" fullWidth />
      <LargeLabel>Contact</LargeLabel>
      {mode === "create" && (
        <TextInput
          source="pair:e-mail"
          helperText="Votre adresse mail n'apparaîtra pas publiquement. On pourra vous écrire via un formulaire de contact."
          fullWidth
        />
      )}
      <TextInput source="pair:aboutPage" fullWidth />
    </SimpleForm>
  );
};

export default EducationNationalForm;
