import React from "react";
import {
  SimpleForm,
  TextInput,
  SelectInput,
  AutocompleteInput,
  Toolbar,
  required,
} from "react-admin";
import MarkdownInput from "ra-input-markdown";
import { LargeLabel } from "@semapps/archipelago-layout";
import { ReferenceInput } from "@semapps/semantic-data-provider";
import {
  teachingLevel,
  structureType,
  academies,
  schoolType,
} from "../../../constants";
import ReferenceQuickCreateInput from "../../../pair/ReferenceQuickCreateInput";
import PairLocationInput from "../../../pair/PairLocationInput";

// * **Education nationale**
//     * Profil
//         * Prénom ou nom
//             * champ obligatoire
//         * Académie
//             * champ obligatoire
//             * Liste des 30 ou champ libre pour autre pays
//         * Poste occupé : réponse libre
//             * champ obligatoire
//         * Niveau : maternelle, élémentaire, collège, lycée, supérieur
//             * champ obligatoire
//         * Type d'école : école publique, école privée sous contrat, ecole privée hors contrat
//             * champ obligatoire
//         * Nom de l'école
//             * champ obligatoire
//         * Commune de l'école
//             * champ obligatoire
//     * Pratique de la classe dehors
//         * Où faite-vous classe dehors ?
//             * Commentaire sous la question "C'est ce lieu qui apparaîtra sur la carte"
//             * question obligatoire
//         * Depuis quand pratiquez-vous la classe dehors ?
//         * A quelle fréquence ?
//         * Avez-vous un site internet, un blog et/ou un compte sur un réseau social ?
//         * Pour partager une pratique pédagogique avec un collègue et nourrir la bibiliothèque collaborative, cliquez [ici](https://wiki.faire-ecole.org/wiki/Sp%C3%A9cial:AjouterPage?form=Activit%C3%A9&page_name=)
//     * Contact
//         * Adresse mail
//         * champ obligatoire
//         *

export const EducationNationalForm = ({ mode, ...rest }) => (
  <SimpleForm
    {...rest}
    redirect="show"
    toolbar={<Toolbar alwaysEnableSaveButton />} // Always enable save as there are problems with ReferenceQuickCreateInput
  >
    <LargeLabel>Profil</LargeLabel>
    <TextInput
      source="pair:label"
      label="Prénom ou Nom"
      validate={required()}
      fullWidth
    />
    <AutocompleteInput
      source="TODO"
      label="Académie"
      validate={required()}
      choices={academies}
    />
    <TextInput
      source="TODO"
      label="Poste occupé"
      validate={required()}
      fullWidth
    />
    <SelectInput
      source="cd:teachingLevel"
      validate={required()}
      choices={teachingLevel}
    />
    <SelectInput
      source="cd:structureType"
      label="Type d’école"
      validate={required()}
      choices={schoolType}
    />
    <TextInput
      source="cd:structureName"
      label="Nom de l’école"
      validate={required()}
      fullWidth
    />
    <TextInput
      source="cd:structureLocality"
      label="Commune de l’école"
      validate={required()}
      fullWidth
    />
    <LargeLabel>Pratique de la classe dehors</LargeLabel>
    <ReferenceQuickCreateInput
      reference="Place"
      source="pair:hasLocation"
      selectOptionText="pair:label"
      label="Où faite-vous classe dehors?"
      perPage={1000}
    >
      <PairLocationInput
        label="Adresse"
        source="pair:hasPostalAddress"
        fullWidth
      />
      <TextInput
        label="Nom"
        source="pair:label"
        validate={required()}
        fullWidth
      />
      <MarkdownInput
        label="Description"
        source="pair:description"
        multiline
        fullWidth
      />
    </ReferenceQuickCreateInput>
    <TextInput
      source="cd:practiceTime"
      label="Depuis quand pratiquez-vous la classe dehors ?"
      fullWidth
    />
    <TextInput
      source="cd:practiceFrequency"
      label="A quelle fréquence ?"
      fullWidth
    />
    <TextInput
      source="pair:webPage"
      label="Avez-vous un site internet, un blog et/ou un compte sur un réseau social ?"
      fullWidth
    />
    {mode === "create" && (
      <>
        <LargeLabel>Contact</LargeLabel>
        <TextInput
          source="pair:e-mail"
          label="Adresse mail"
          helperText="Votre adresse mail n'apparaîtra pas publiquement. On pourra vous écrire via un formulaire de contact."
          fullWidth
        />
      </>
    )}
  </SimpleForm>
);

export default EducationNationalForm;
