import React from "react";
import { DateField, ReferenceField, TextField } from "react-admin";
import { Avatar } from "@material-ui/core";
import { List, SimpleList, ListActions } from "@semapps/archipelago-layout";
import EventIcon from "@material-ui/icons/Event";
import EventFilterSidebar from "./EventFilterSidebar";
import isSameDay from "date-fns/isSameDay";
import format from "date-fns/format";

const EventList = (props) => (
  <List
    title="Calendrier"
    aside={<EventFilterSidebar />}
    sort={{ field: "pair:startDate", order: "DESC" }}
    actions={<ListActions exporter={false} />}
    {...props}
  >
    <SimpleList
      primaryText={(record) => record["pair:label"]}
      secondaryText={(record) => (
        <ReferenceField
          record={record}
          reference="Organization"
          basePath="Organization" // Hack to get access to to Organization data
          source="pair:operatedBy"
          linkType="show"
        >
          <TextField source="pair:label" />
        </ReferenceField>
      )}
      tertiaryText={(record) => {
        if (
          record["pair:startDate"] &&
          record["pair:endDate"] &&
          isSameDay(
            new Date(record["pair:startDate"]),
            new Date(record["pair:endDate"])
          )
        ) {
          return `Le\xa0${format(
            new Date(record["pair:startDate"]),
            "dd/MM/yyyy"
          )}\xa0de\xa0${format(
            new Date(record["pair:startDate"]),
            "kk:mm"
          )}\xa0à\xa0${format(new Date(record["pair:endDate"]), "kk:mm")}`;
          // return (
          //   <>
          //     Le&nbsp;
          //     <FunctionField
          //       record={record}
          //       source="pair:startDate"
          //       render={(record) =>
          //         format(new Date(record["pair:startDate"]), "dd/MM/yyyy")
          //       }
          //     />
          //     &nbsp;de&nbsp;
          //     <FunctionField
          //       record={record}
          //       source="pair:startDate"
          //       render={(record) =>
          //         format(new Date(record["pair:startDate"]), "kk:mm")
          //       }
          //     />
          //     &nbsp;à&nbsp;
          //     <FunctionField
          //       record={record}
          //       source="pair:endDate"
          //       render={(record) =>
          //         format(new Date(record["pair:endDate"]), "kk:mm")
          //       }
          //     />
          //   </>
          // );
        }
        return (
          <>
            Du&nbsp;
            <DateField
              record={record}
              source="pair:startDate"
              options={{
                year: "numeric",
                month: "2-digit",
                day: "2-digit",
                hour: "2-digit",
                minute: "2-digit",
              }}
            />
            &nbsp;au&nbsp;
            <DateField
              record={record}
              source="pair:endDate"
              options={{
                year: "numeric",
                month: "2-digit",
                day: "2-digit",
                hour: "2-digit",
                minute: "2-digit",
              }}
            />
          </>
        );
      }}
      leftAvatar={() => (
        <Avatar width="100%">
          <EventIcon />
        </Avatar>
      )}
      linkType="show"
    />
  </List>
);

export default EventList;
