import React, { useState } from "react";
import { SimpleForm, TextInput, useInput } from "react-admin";
import PairLocationInput from "../../pair/PairLocationInput";

import ReactMde from "react-mde";
import * as Showdown from "showdown";

import { useField } from "react-final-form";
import "react-mde/lib/styles/css/react-mde-all.css";
import { FormLabel, FormHelperText, FormControl } from "@material-ui/core";

const MarkdownInput = ({ source, helperText, label, defaultValue }) => {
  const {
    input: { value = "", onBlur, onFocus, onChange },
    meta: { error, submitError, touched },
    isRequired,
  } = useInput({ source, defaultValue: defaultValue || "" });

  const [tab, setTab] = useState("write");

  const converter = new Showdown.Converter({
    tables: true,
    simplifiedAutoLink: true,
    strikethrough: true,
    tasklists: true,
  });

  return (
    <FormControl
      error={!!(touched && (error || submitError))}
      required={isRequired}
    >
      {label !== "" && label !== false && <FormLabel>{label}</FormLabel>}
      <ReactMde
        onTabChange={(tab) => setTab(tab)}
        generateMarkdownPreview={(markdown) =>
          Promise.resolve(converter.makeHtml(markdown))
        }
        selectedTab={tab}
        childProps={{
          textArea: {
            onBlur: onBlur,
            onFocus: onFocus,
          },
        }}
        value={value}
        onChange={onChange}
      />
      <FormHelperText variant="outlined">
        {touched && error
          ? error
          : typeof helperText === "string"
          ? helperText
          : null}
      </FormHelperText>
    </FormControl>
  );
};

export const validatePlaceForm = (values) => {
  const errors = {};
  console.log(values);
  if (!values["pair:hasPostalAddress"]) {
    errors["pair:hasPostalAddress"] = "required";
  }
  if (!values["pair:label"]) {
    errors["pair:label"] = "required";
  }
  if (!values["pair:description"]) {
    errors["pair:description"] = "required";
  }
  return errors;
};

export const PlaceFields = ({ ...props }) => (
  <>
    <p>Merci de remplir tous les champs</p>
    <PairLocationInput
      label="Adresse"
      source="pair:hasPostalAddress"
      fullWidth
    />
    <TextInput label="Nom" source="pair:label" fullWidth />
    <MarkdownInput
      multiline
      label="Description"
      source="pair:description"
      fullWidth
      helperText="Pourquoi ce lieu est-il spÃ©cifique ?"
    />
  </>
);

export const PlaceForm = ({ mode, ...rest }) => (
  <SimpleForm {...rest} redirect="show" validate={validatePlaceForm}>
    <PlaceFields />
  </SimpleForm>
);

export default PlaceForm;
